pub enum AccountShrinkThreshold {
    TotalSpace {
            shrink_ratio: f64,
        },
    IndividualStore {
            shrink_ratio: f64,
        },
}

pub enum LoadedAccountAccessor<'a> {
    Stored(Option<(Arc<AccountStorageEntry>, usize)>),
    Cached(Option<Cow>),
}

pub enum BankHashVerificationError {
    MismatchedAccountHash,
    MismatchedBankHash,
    MissingBankHash,
    MismatchedTotalLamports(u64, u64),
}

pub enum UpsertReclaim {
    PreviousSlotEntryWasCached,
    PopulateReclaims,
    IgnoreReclaims,
}

pub enum ScanError {
    SlotRemoved {
            slot: Slot,
            bank_id: BankId,
        },
    Aborted(String),
}

pub enum IndexKey {
    ProgramId(Pubkey),
    SplTokenMint(Pubkey),
    SplTokenOwner(Pubkey),
}

pub enum AccountIndexGetResult<T: IndexValue> {
    Found(ReadAccountMapEntry<T>, usize),
    NotFound,
}

pub enum PreAllocatedAccountMapEntry<T: IndexValue> {
    Entry(AccountMapEntry<T>),
    Raw((Slot, T)),
}

pub enum TransactionExecutionResult {
    Executed {
            details: TransactionExecutionDetails,
            executors: Rc<RefCell<Executors>>,
        },
    NotExecuted(TransactionError),
}

pub enum TransactionLogCollectorFilter {
    All,
    AllWithVotes,
    None,
    OnlyMentionedAddresses,
}

pub enum RewardCalculationEvent<'a, 'b> {
    Staking(&'a Pubkey, &'b InflationPointCalculationEvent),
}

pub enum BuiltinFeatureTransition {
    Add {
            builtin: Builtin,
            feature_id: Pubkey,
        },
    RemoveOrRetain {
            previously_added_builtin: Builtin,
            addition_feature_id: Pubkey,
            removal_feature_id: Pubkey,
        },
}

pub enum InsertNewEntryResults {
    DidNotExist,
    ExistedNewEntryZeroLamports,
    ExistedNewEntryNonZeroLamports,
}

pub enum Error {
    InstructionError(InstructionError),
    InvalidDelegation(Box<StakeState>),
    InvalidOwner(Pubkey),
}
